// Note: Dirty Boxing logo uses native Text component instead of SVG
// (SVG text elements don't render properly in react-native-svg)

interface PromotionLogoProps {
  promotion: string;
  size?: number;
  style?: any;
  color?: string; // Override fill color (e.g., '#FFFFFF' for white)
}

export function PromotionLogo({ promotion, size = 40, style, color }: PromotionLogoProps) {
  const normalizedPromotion = promotion?.toUpperCase() || '';

  let svgXml: string | null = null;
  let pngSource: ImageSourcePropType | null = null;
  let aspectRatio = 1;

  switch (normalizedPromotion) {
    case 'ZUFFA BOXING':
    case 'ZUFFA_BOXING':
    case 'ZUFFA':
      pngSource = ZUFFA_BOXING_LOGO;
      aspectRatio = 1080 / 350; // ~3.09
      break;
    case 'UFC':
      svgXml = UFC_LOGO;
      aspectRatio = 1675 / 580; // ~2.89
      break;
    case 'PFL':
      svgXml = PFL_LOGO;
      aspectRatio = 1180.62 / 486.84; // ~2.42
      break;
    case 'BKFC':
      svgXml = BKFC_LOGO;
      aspectRatio = 1031 / 247; // ~4.17
      break;
    case 'ONE':
    case 'ONE FC':
    case 'ONE CHAMPIONSHIP':
      svgXml = ONE_LOGO;
      aspectRatio = 732 / 380; // ~1.93
      break;
    case 'GOLDEN BOY':
    case 'GOLDEN BOY PROMOTIONS':
      svgXml = GOLDEN_BOY_LOGO;
      aspectRatio = 352.8 / 75.7; // ~4.66
      break;
    case 'OKTAGON':
    case 'OKTAGON MMA':
      svgXml = OKTAGON_LOGO;
      aspectRatio = 450 / 130; // ~3.46
      break;
    case 'PBC':
    case 'PREMIER BOXING CHAMPIONS':
      svgXml = PBC_LOGO;
      aspectRatio = 450 / 130; // ~3.46
      break;
    case 'RIZIN':
    case 'RIZIN FF':
    case 'RIZIN FIGHTING FEDERATION':
      svgXml = RIZIN_LOGO;
      aspectRatio = 800 / 310; // ~2.58
      break;
    case 'MVP':
    case 'MOST VALUABLE PROMOTIONS':
      svgXml = MVP_LOGO;
      aspectRatio = 800 / 330; // ~2.42 (matches cropped viewBox)
      break;
    case 'KARATE COMBAT':
      svgXml = KARATE_COMBAT_LOGO;
      aspectRatio = 1125 / 380; // ~2.96
      break;
    case 'MATCHROOM':
    case 'MATCHROOM BOXING':
      svgXml = MATCHROOM_LOGO;
      aspectRatio = 358 / 124; // ~2.89
      break;
    case 'TOP RANK':
    case 'TOP_RANK':
    case 'TOP RANK BOXING':
      svgXml = TOP_RANK_LOGO;
      aspectRatio = 4; // Compact width
      break;
    case 'DBX':
    case 'DIRTY BOXING':
    case 'DIRTY BOXING CHAMPIONSHIP':
      svgXml = DBX_LOGO;
      aspectRatio = 378 / 248; // ~1.52
      break;
    case 'SHOWTIME':
    case 'SHOWTIME BOXING':
      svgXml = SHOWTIME_LOGO;
      aspectRatio = 1064 / 395; // ~2.69
      break;
    default:
      return null;
  }

  const width = size * aspectRatio;
  const height = size;

  // Handle PNG logos
  if (pngSource) {
    return (
      <View style={[styles.container, style]}>
        <Image
          source={pngSource}
          style={{ width, height }}
          resizeMode="contain"
        />
      </View>
    );
  }

  // Apply color override if specified for SVG
  if (color && svgXml) {
    // Replace all fill colors with the specified color
    svgXml = svgXml.replace(/fill="[^"]+"/g, `fill="${color}"`);
  }

  return (
    <View style={[styles.container, style]}>
      <SvgXml xml={svgXml} width={width} height={height} />
    </View>
  );
}

const styles = StyleSheet.create({
  container: {
    justifyContent: 'center',
    alignItems: 'center',
  },
  textLogo: {
    justifyContent: 'center',
    alignItems: 'center',
  },
  textLogoText: {
    fontWeight: 'bold',
    letterSpacing: 1,
  },
});

export default PromotionLogo;
