// Note: Dirty Boxing logo uses native Text component instead of SVG
// (SVG text elements don't render properly in react-native-svg)

interface PromotionLogoProps {
  promotion: string;
  size?: number;
  style?: any;
  color?: string; // Override fill color (e.g., '#FFFFFF' for white)
}

export function PromotionLogo({ promotion, size = 40, style, color }: PromotionLogoProps) {
  const normalizedPromotion = promotion?.toUpperCase() || '';

  // Handle text-based logos (SVG text doesn't render well in react-native-svg)
  if (normalizedPromotion === 'DIRTY BOXING' || normalizedPromotion === 'DIRTY BOXING CHAMPIONSHIP') {
    const aspectRatio = 4.0;
    const width = size * aspectRatio;
    const height = size;
    const fontSize = size * 0.35;

    return (
      <View style={[styles.container, style]}>
        <View style={[styles.textLogo, { width, height, backgroundColor: color || '#8B0000', borderRadius: 4 }]}>
          <Text style={[styles.textLogoText, { fontSize, color: color ? '#8B0000' : '#FFFFFF' }]}>
            DIRTY BOXING
          </Text>
        </View>
      </View>
    );
  }

  let svgXml: string | null = null;
  let aspectRatio = 1;

  switch (normalizedPromotion) {
    case 'UFC':
      svgXml = UFC_LOGO;
      aspectRatio = 1675 / 580; // ~2.89
      break;
    case 'PFL':
      svgXml = PFL_LOGO;
      aspectRatio = 1180.62 / 486.84; // ~2.42
      break;
    case 'BKFC':
      svgXml = BKFC_LOGO;
      aspectRatio = 1031 / 247; // ~4.17
      break;
    case 'ONE':
    case 'ONE FC':
    case 'ONE CHAMPIONSHIP':
      svgXml = ONE_LOGO;
      aspectRatio = 732 / 380; // ~1.93
      break;
    case 'GOLDEN BOY':
    case 'GOLDEN BOY PROMOTIONS':
      svgXml = GOLDEN_BOY_LOGO;
      aspectRatio = 352.8 / 75.7; // ~4.66
      break;
    case 'OKTAGON':
    case 'OKTAGON MMA':
      svgXml = OKTAGON_LOGO;
      aspectRatio = 450 / 130; // ~3.46
      break;
    case 'PBC':
    case 'PREMIER BOXING CHAMPIONS':
      svgXml = PBC_LOGO;
      aspectRatio = 450 / 130; // ~3.46
      break;
    case 'RIZIN':
    case 'RIZIN FF':
    case 'RIZIN FIGHTING FEDERATION':
      svgXml = RIZIN_LOGO;
      aspectRatio = 500 / 112; // ~4.46
      break;
    case 'MVP':
    case 'MOST VALUABLE PROMOTIONS':
      svgXml = MVP_LOGO;
      aspectRatio = 600 / 400; // 1.5
      break;
    case 'KARATE COMBAT':
      svgXml = KARATE_COMBAT_LOGO;
      aspectRatio = 421 / 237; // ~1.78
      break;
    case 'MATCHROOM':
    case 'MATCHROOM BOXING':
      svgXml = MATCHROOM_LOGO;
      aspectRatio = 358 / 124; // ~2.89
      break;
    case 'TOP RANK':
    case 'TOP_RANK':
    case 'TOP RANK BOXING':
      svgXml = TOP_RANK_LOGO;
      aspectRatio = 4; // Compact width
      break;
    default:
      return null;
  }

  // Apply color override if specified
  if (color && svgXml) {
    // Replace all fill colors with the specified color
    svgXml = svgXml.replace(/fill="[^"]+"/g, `fill="${color}"`);
  }

  const width = size * aspectRatio;
  const height = size;

  return (
    <View style={[styles.container, style]}>
      <SvgXml xml={svgXml} width={width} height={height} />
    </View>
  );
}

const styles = StyleSheet.create({
  container: {
    justifyContent: 'center',
    alignItems: 'center',
  },
  textLogo: {
    justifyContent: 'center',
    alignItems: 'center',
  },
  textLogoText: {
    fontWeight: 'bold',
    letterSpacing: 1,
  },
});

export default PromotionLogo;
